import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {

  loginForm = new FormGroup({
    email: new FormControl('', [Validators.required, Validators.email]),
    password: new FormControl('', Validators.required),
  })

  constructor(){
    this.loginForm.valueChanges.subscribe(value => {
      console.log(value);
    });
  }

  login(){

  }
}
<form [formGroup]="loginForm" (submit)="login()" aria-autocomplete="new-password">
    <h1>Customer Login</h1>
    <div class="form-control">
        <label for="email">Email</label>
        <input formControlName="email" id="email" autocomplete="new-password" type="email">
    </div>

    <div class="form-control">
        <label for="password">Password</label>
        <input formControlName="password" id="password" autocomplete="new-password" type="email">
    </div>
<button type="submit">Login</button>
</form>
